import { Container, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useQuery} from 'react-query'
import Swal from 'sweetalert2'
import {API} from '../config/api'
import { useParams } from "react-router-dom";

export default function MyArticle() {

    const [idDelete, setIdDelete] = useState(null);

    let { data: products, refetch } = useQuery('productsAdminCache', async () => {
        const response = await API.get('/products');
        return response.data.data;
      });

      const handleDelete = (id) => {
        setIdDelete(id);
        handleShow();
      };

    return (
        <Container>
            <h2 className='text-bold text-color-pink mt-5'>My Article</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Title</th>
                        <th style={{ width: "150px" }}>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {articles?.length != 0 &&
                        articles?.map((item, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{item.Title}</td>
                                <td>
                                    <div className="dropdown">
                                        <span className="dropdown" data-bs-toggle="dropdown" aria-expanded="false">
                                            <img src={process.env.PUBLIC_URL + "/images/searc.png"} alt="search" style={{ width: "20px" }} />
                                        </span>
                                        <ul className="dropdown-menu">
                                            <li>
                                                <Link className="d-flex justify-content-center" style={{ textDecoration: "none" }}>
                                                    <button onClick={() => deleteArticle(item.id)} className="text-bold" style={{ border: "none", color: "white", borderRadius: "10px", padding: "4px", backgroundColor: "#0ACF83" }}>
                                                        Delete Article
                                                    </button>
                                                </Link>
                                            </li>
                                        </ul>
                                    </div>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </Table>
        </Container>
    )
}
