import { Container, Table } from "react-bootstrap";
import { Link } from "react-router-dom";


export default function MyArticle() {

    let { data: articles, refetch } = useQuery('articlesCache', async () => {
        const response = await API.get('/articles');
        return response.data.data;
    });

    const deleteArticle = useMutation(async (id) => {
        try {
            const response = await API.delete('/article/' + id);
            Swal.fire({
                title: 'Success!',
                text: 'Product berhasil dihapus',
                icon: 'success',
                confirmButtonText: 'Kembali'
            })
            console.log(response)
            refetch()
        } catch (error) {
            Swal.fire({
                title: 'Success!',
                text: 'Product gagal dihapus',
                icon: 'success',
                confirmButtonText: 'Kembali'
            })
        }
    })

    return (
        <Container>
            <h2 className='text-bold text-color-pink mt-5'>My Article</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Title</th>
                        <th style={{ width: "150px" }}>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>Mark</td>
                        <td>
                            <div className="dropdown">
                                <span className="dropdown" data-bs-toggle="dropdown" aria-expanded="false">
                                    <img src={process.env.PUBLIC_URL + "/images/searc.png"} alt="search" style={{ width: "20px" }} />
                                </span>
                                <ul className="dropdown-menu">
                                    <li>
                                        <Link className="d-flex justify-content-center" style={{ textDecoration: "none" }}>
                                            <button onClick={deleteArticle(item.id)} className="text-bold" style={{ border: "none", color: "white", borderRadius: "10px", padding: "4px", backgroundColor: "#0ACF83" }}>
                                                Delete Article
                                            </button>
                                        </Link>
                                    </li>
                                </ul>
                            </div>
                        </td>
                    </tr>
                </tbody>
            </Table>
        </Container>
    )
}
