import { Container, Form } from "react-bootstrap";
import { useMutation } from "react-query";
import { Link, useNavigate } from 'react-router-dom'

export default function AddArticle() {

    let navigate = useNavigate()

    const [preview, setPriview] = useState(null)
    const [form, setForm] = useState({
        title: '',
        image: '',
        description: '',
    })

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]:
                e.target.type === 'file' ? e.target.files : e.target.value,
        });

        // Create image url for preview
        if (e.target.type === 'file') {
            let url = URL.createObjectURL(e.target.files[0]);
            setPreview(url);
        }
    }

    const handleSubmit = useMutation(async (e) => {
        try {
            e.preventDefault()

            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            }

            // Store data with FormData as object
            const formData = new FormData()
            formData.set('image', form.image[0], form.image[0].name)
            formData.set('title', form.title)
            formData.set('description', form.description)

            const responser = await API.post('/article', formData, config)
            console.log("add article success : ", response);
            Swal.fire({
                title: 'Success!',
                text: 'Article berhasil ditambahkan',
                icon: 'success',
                confirmButtonText: 'Kembali'
            })

            navigate('/')
        } catch (e) {
            console.log("add article failed : ", e);
            Swal.fire({
                title: 'Error!',
                text: 'Article gagal ditambahkan',
                icon: 'error',
                confirmButtonText: 'Kembali'
            })
        }
    })

    return (
        <Container>
            <h2 className='text-bold text-color-pink mt-5'>Add Article</h2>
            <Form onSubmit={(e) => handleSubmit.mutate(e)} className="my-4">
                <Form.Group className="mb-3" controlId="formBasicTitle">
                    <Form.Label className="text-bold">Title</Form.Label>
                    <Form.Control type="text" className="bg-input-form"  onChange={handleChange} required/>
                </Form.Group>
                <div className="col-md-4">
                    <Form.Group className="mb-3" controlId="formBasicImage">
                        <Form.Label className="text-bold">Image Thumbnail</Form.Label>
                        <Form.Control type="file" className="bg-input-form" onChange={handleChange} required/>
                    </Form.Group>
                </div>
                <Form.Group className="mb-3" controlId="formBasicDescription">
                    <Form.Label className="text-bold">Description</Form.Label>
                    <Form.Control
                        as="textarea"
                        style={{ height: '100px' }}
                        className="bg-input-form"
                        onChange={handleChange} 
                        required
                    />
                </Form.Group>
                <Link className="d-flex justify-content-center" style={{ textDecoration: "none" }}>
                    <button className="bg-color-pink text-bold" style={{ border: "none", color: "white", borderRadius: "10px", padding: "8px 90px" }}>
                        Post
                    </button>
                </Link>
            </Form>
        </Container>
    )
}
